import logo from './logo.svg';
import './App.css';

const form = document.querySelector("form");
const inputCity = document.querySelector('#city');
const inputCountry = document.querySelector('#country');
const history = document.querySelector(".history ul");
const msg = document.querySelector(".message");

form.addEventListener("submit", e => {
  e.preventDefault();
  let inputCityVal = inputCity.value;
  let inputCountryVal = inputCountry.value;

  const url = `https://api.openweathermap.org/data/2.5/weather?q=${inputCityVal},${inputCountryVal}&appid=22dc15f16b68c17e6adeaca94240569a`;

  fetch(url)
    .then(response => response.json())
    .then(data => {
      console.log(data);
      const { weather, main } = data;
      const li = document.createElement("li");
      const markup = `
        <li>
           <span class="weather">${weather[0]["main"]}</span>
           <span class="desc">${weather[0]["description"]}</span>
           <span class="temp">${main.temp_min} ~ ${main.temp_max}</span>
           <span class="humid">${main.humidity}</span>
           <span class="time">${new Date().toLocaleTimeString()}</span>
        </li>
      `;
      li.innerHTML = markup;
      history.appendChild(li);
    })
    .catch((err) => {
      msg.append('404');
      console.log(err);
    });
  form.reset();
});
